" loading="eager" width="644" height="573" alt="" class="_404-not-found-image" /></div></div></div></section><?php include 'footer.php'; ?></div><script src="https://d3e54v103j8qbb.cloudfront.net/js/jquery-3.5.1.min.dc5e7f18c8.js?site=686b7d020ea82bd1ba5003a8" type="text/javascript" integrity="sha256-9/aliU8dGd2tb6OSsuzixeV4y/faTqgFtohetphbbj0=" crossorigin="anonymous"></script><script src="https://wubflow-shield.nocodexport.dev/686b7d020ea82bd1ba5003a8/js/webflow.schunk.57d5559d2f0cd9f8.js" type="text/javascript"></script><script src="https://wubflow-shield.nocodexport.dev/686b7d020ea82bd1ba5003a8/js/webflow.schunk.5e1e399f410ff42d.js" type="text/javascript"></script><script src="https://wubflow-shield.nocodexport.dev/686b7d020ea82bd1ba5003a8/js/webflow.cf12e98f.009eb7ea97751c1b.js" type="text/javascript"></script><script src="https://wubflow-shield.nocodexport.dev/gsap/3.13.0/gsap.min.js" type="text/javascript"></script><script src="https://wubflow-shield.nocodexport.dev/gsap/3.13.0/ScrollTrigger.min.js" type="text/javascript"></script><script src="https://wubflow-shield.nocodexport.dev/gsap/3.13.0/SplitText.min.js" type="text/javascript"></script><script src="https://wubflow-shield.nocodexport.dev/gsap/3.13.0/Observer.min.js" type="text/javascript"></script><script type="text/javascript">gsap.registerPlugin(ScrollTrigger,SplitText,Observer);</script><script>
// GSAP Template Animation Code - Consolidated for Webflow Validation
// Register all GSAP plugins at the top
gsap.registerPlugin(ScrollTrigger, SplitText, Observer);

// Initialize all animations when DOM is ready
document.addEventListener("DOMContentLoaded", function() {
    initMarqueeAnimation();
    initCounterAnimation();
});

// ============================================
// MARQUEE SCROLL ANIMATION
// ============================================
function initMarqueeAnimation() {
    const marqueeObject = { value: 1 };
    
    // Create marquee timeline with GSAP
    const marqueeTimeline = gsap.timeline({
        repeat: -1,
        onReverseComplete: () => {
            marqueeTimeline.progress(1);
        }
    });
    
    // Set up marquee animation
    marqueeTimeline.fromTo(
        '.marquee-scroll-item',
        { xPercent: 0 },
        {
            xPercent: -100,
            duration: 50, // Adjust for marquee speed
            ease: 'none'
        }
    );
    
    // Create Observer for scroll interaction
    Observer.create({
        target: window,
        type: 'wheel,scroll,touch',
        onChangeY: (self) => {
            let velocity = self.velocityY * 0.002;
            velocity = gsap.utils.clamp(-40, 40, velocity);
            marqueeTimeline.timeScale(velocity);
            
            let restingSpeed = velocity < 0 ? -1 : 1;
            
            // Animate timeScale change with GSAP
            gsap.fromTo(
                marqueeObject,
                { value: velocity },
                {
                    value: restingSpeed,
                    duration: 1,
                    onUpdate: () => {
                        marqueeTimeline.timeScale(marqueeObject.value);
                    }
                }
            );
        }
    });
    
    // Handle reduced motion preference
    const reduceMotion = window.matchMedia('(prefers-reduced-motion: reduce)');
    if (reduceMotion.matches) {
        marqueeTimeline.pause();
    }
}

// ============================================
// NUMBER COUNTER ANIMATION
// ============================================
function initCounterAnimation() {
    // Helper function for number formatting
    function numberWithCommas(x, decimals = 0) {
        return x.toLocaleString(undefined, {
            minimumFractionDigits: decimals,
            maximumFractionDigits: decimals
        });
    }
    
    // Get all counter elements with GSAP utility
    const counterElements = gsap.utils.toArray(".count-up-number-animation");
    
    if (counterElements.length === 0) return; // Guard clause
    
    // Create ScrollTrigger animations for each counter
    counterElements.forEach((element, index) => {
        const targetValue = parseFloat(element.getAttribute("data-count")) || 100;
        const decimals = (targetValue % 1 !== 0) ? 1 : 0;
        
        // Create counter animation with GSAP
        gsap.fromTo(element,
            { textContent: 0 },
            {
                textContent: targetValue,
                duration: 2,
                ease: "power1.out",
                snap: decimals ? { textContent: 0.1 } : { textContent: 1 },
                delay: index * 0.1,
                scrollTrigger: {
                    trigger: element,
                    start: "top 80%",
                    once: true,
                    toggleActions: "play none none none"
                },
                onUpdate: function() {
                    const currentValue = parseFloat(element.textContent);
                    element.textContent = numberWithCommas(currentValue, decimals);
                }
            }
        );
    });
    
    // Refresh ScrollTrigger after all content loads
    window.addEventListener("load", () => {
        ScrollTrigger.refresh();
    });
}

// ============================================
// ADDITIONAL GSAP UTILITIES
// ============================================
// Batch processing for performance
ScrollTrigger.batch(".animate-on-scroll", {
    onEnter: batch => gsap.to(batch, {
        opacity: 1,
        y: 0,
        stagger: 0.15,
        overwrite: true
    }),
    onLeave: batch => gsap.set(batch, {
        opacity: 0,
        y: 100,
        overwrite: true
    }),
    onEnterBack: batch => gsap.to(batch, {
        opacity: 1,
        y: 0,
        stagger: 0.15,
        overwrite: true
    }),
    onLeaveBack: batch => gsap.set(batch, {
        opacity: 0,
        y: -100,
        overwrite: true
    })
});

// Update on window resize
window.addEventListener("resize", () => {
    ScrollTrigger.refresh();
});
</script>
