// Add dropdown functionality
document.addEventListener('click', function(e) {
  const dropdownToggle = e.target.closest('.dropdown-toggle');
  if (dropdownToggle) {
    e.preventDefault();
    const dropdown = dropdownToggle.closest('.header-dropdown');
    const dropdownList = dropdown.querySelector('.dropdown-content-wrapper');

    if (dropdown && dropdownList) {
      // Close other dropdowns
      document.querySelectorAll('.header-dropdown').forEach(otherDropdown => {
        if (otherDropdown !== dropdown) {
          otherDropdown.classList.remove('w--open');
          const otherList = otherDropdown.querySelector('.dropdown-content-wrapper');
          if (otherList) {
            otherList.style.display = 'none';
            otherList.style.opacity = '0';
          }
        }
      });

      // Toggle current dropdown
      const isOpen = dropdown.classList.contains('w--open');
      if (isOpen) {
        dropdown.classList.remove('w--open');
        dropdownList.style.display = 'none';
        dropdownList.style.opacity = '0';
      } else {
        dropdown.classList.add('w--open');
        dropdownList.style.display = 'block';
        dropdownList.style.opacity = '1';
      }
    }
  }
});

// Close dropdown when clicking outside
document.addEventListener('click', function(e) {
  if (!e.target.closest('.header-dropdown')) {
    document.querySelectorAll('.header-dropdown').forEach(dropdown => {
      dropdown.classList.remove('w--open');
      const dropdownList = dropdown.querySelector('.dropdown-content-wrapper');
      if (dropdownList) {
        dropdownList.style.display = 'none';
        dropdownList.style.opacity = '0';
      }
    });
  }
});
</script>
